#include "sha1.h"

#define MAX_MODULUS 256

static void check_pkcs1_sig(const cf_chash *hash,
                            const void *msg, size_t nmsg,
                            const void *encoding, size_t nmodulus)
{
  uint8_t got_encoding[MAX_MODULUS];
  assert(sizeof got_encoding >= nmodulus);

  cf_rsassa_pkcs1_encode(msg, nmsg, got_encoding, nmodulus);
  TEST_CHECK(memcmp(encoding, got_encoding, nmodulus) == 0);

  unsigned verf;
  verf = cf_rsassa_pkcs1_verify(encoding, nmodulus, msg, nmsg);
  TEST_CHECK(verf == 0);

  /* wrong msg */
  uint8_t wrong_msg[sizeof got_encoding];
  memcpy(wrong_msg, encoding, nmsg);
  wrong_msg[4] ^= 0xff;
  verf = cf_rsassa_pkcs1_verify(encoding, nmodulus, wrong_msg, nmsg);
  TEST_CHECK(verf == 1);

  /* wrong sig */
  got_encoding[0] ^= 0xff;
  verf = cf_rsassa_pkcs1_verify(got_encoding, nmodulus, msg, nmsg);
  TEST_CHECK(verf == 1);
}

