static void check_pkcs1_enc(const void *msg, size_t nmsg,
                            const void *random, size_t nrandom,
                            const void *encoding, size_t nmodulus)
{
  /* check we get the right encoding */
  fixed_rng rng = make_fixed_rng(random, nrandom);
  uint8_t got_encoding[MAX_MODULUS];
  cf_rsaes_pkcs1_encode(&rng.rng, msg, nmsg, got_encoding, nmodulus);
  TEST_CHECK(memcmp(got_encoding, encoding, nmodulus) == 0);

  /* check we get the right message from decoding */
  uint8_t *got_msg = got_encoding;
  size_t got_nmsg;
  unsigned verf = cf_rsaes_pkcs1_decode(encoding, nmodulus, got_msg, &got_nmsg);
  TEST_CHECK(verf == 0);
  TEST_CHECK(got_nmsg == nmsg);
  TEST_CHECK(got_nmsg == nmsg && memcmp(msg, got_msg, nmsg) == 0);
}

