static void check_oaep_enc(const void *msg, size_t nmsg,
                           const void *seed, size_t nseed,
                           const void *enc, size_t nmodulus)
{
  uint8_t buf[MAX_MODULUS];
  fixed_rng rng = make_fixed_rng(seed, nseed);
  cf_rsaes_oaep_encode(&cf_sha1, &rng.rng,
                       msg, nmsg,
                       buf, nmodulus);

  TEST_CHECK(memcmp(buf, enc, nmodulus) == 0);

  unsigned verf;
  size_t got_nmsg;
  verf = cf_rsaes_oaep_decode(&cf_sha1,
                              enc, nmodulus,
                              buf, &got_nmsg);
  TEST_CHECK(verf == 0);
  TEST_CHECK(got_nmsg == nmsg);
  TEST_CHECK(memcmp(msg, buf, nmsg) == 0);

  /* wrong Y */
  memcpy(buf, enc, nmodulus);
  buf[0] ^= 0x01;
  verf = cf_rsaes_oaep_decode(&cf_sha1,
                              buf, nmodulus,
                              buf, &got_nmsg);
  TEST_CHECK(verf == 1);

  /* wrong lHash */
  memcpy(buf, enc, nmodulus);
  buf[1 + 20 + 1] ^= 0x01;
  verf = cf_rsaes_oaep_decode(&cf_sha1,
                              buf, nmodulus,
                              buf, &got_nmsg);
  TEST_CHECK(verf == 1);

  /* wrong salt */
  memcpy(buf, enc, nmodulus);
  buf[2] ^= 0x01;
  verf = cf_rsaes_oaep_decode(&cf_sha1,
                              buf, nmodulus,
                              buf, &got_nmsg);
  TEST_CHECK(verf == 1);

}

